package jarduera2_3;
import java.util.Scanner;
public class jarduera13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Programa bat idatzi 0-tik erabiltzaileak sartutako zenbakirarteko zenbakiak ateratzen dituena
		
		int zenbakia, i;
		
		System.out.println("Idatzi zenbaki bat, 0-tik sartutako zenbakirarteko zenbakiak ateratzeko");
		
		Scanner teklatua = new Scanner(System.in);
		zenbakia = teklatua.nextInt();
		
		for (i = 0 ; i <= zenbakia; i++) {
			System.out.println(i);
		}

		
		
		
		
		
		
	}

}
