package jarduera2_3;

public class jarduera6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Programa bat idatzi 7aren biderkatze taula idazten ateratzen duena
		
		int zenbakia, i, emaitza;
		
		zenbakia = 7;
		i = 1;
		
		while (i<=10) {
		emaitza = zenbakia * i;
		System.out.println(zenbakia + "x" + i + "=" + emaitza);
		i++;

	}
		

}
}
